mapper.select:
SELECT COUNT(*)
FROM trip_applications
WHERE event_id = #{eventId}

SELECT *
FROM trips
WHERE user_id = #{userId}
AND event_id = #{eventId}
LIMIT 1

SELECT *
FROM trip_applications
WHERE user_id = #{userId}
AND event_id = #{eventId}
LIMIT 1

SELECT COUNT(*)
FROM trip_applications
WHERE event_id = #{eventId}

SELECT COUNT(*) 
FROM trip_applications
WHERE user_id = #{userId}
  AND event_id = #{eventId}

mapper.update:
UPDATE trips
SET status = #{status}
WHERE event_id = #{eventId}

UPDATE trip_applications
SET status = #{status}
WHERE user_id = #{userId}
AND event_id = #{eventId}

UPDATE trips
SET current_people = #{count}
WHERE event_id = #{eventId}
